sp_widget.client_script:
api.controller = function() {
  /*global api*/
  var c = this;

  // Initialize controller
  c.data.ready = true;

  // Helper function to open settings pages
  c.openSettingsPage = function(url) {
    if (url) {
      window.open(url, '_blank');
    }
  };
};

