sp_widget.script:
(function() {
  /*global gs, data, console*/
  try {
    var status = new global.aiabStatus();
    data.settingsTitle = 'AI In A Box Settings';

    // Features (streaming, summarize, chat)
    data.features = status.featuresAndProperties;

    // Tool Calling (callbackAuth + serviceAccount + helperScript)
    var statusResult = status.status();
    if (statusResult && statusResult.callbackAuth && statusResult.serviceAccount && statusResult.helperScript) {
      var authOk = statusResult.callbackAuth.className.indexOf('btn-success') >= 0;
      var accountOk = statusResult.serviceAccount.className.indexOf('btn-success') >= 0;
      var scriptOk = statusResult.helperScript.className.indexOf('btn-success') >= 0;
      data.toolCalling = {
        enabled: authOk && accountOk && scriptOk,
        callbackAuth: statusResult.callbackAuth,
        serviceAccount: statusResult.serviceAccount,
        helperScript: statusResult.helperScript
      };
    }

    // Config links
    data.coreSettings = [
      {
        label: 'System Properties',
        url: '/system_properties_ui.do?sysparm_title=Properties&sysparm_category=AI%20In%20A%20Box&sysparm_domain_restore=false&sysparm_stack=no',
        buttonText: 'Configure'
      },
      {
        label: 'AI Inferences',
        url: '/u_ai_inference_list.do',
        buttonText: 'View'
      },
      {
        label: 'AI Tools',
        url: '/u_ai_tool_list.do',
        buttonText: 'Manage'
      }
    ];

  } catch (error) {
    data.error = 'Error loading settings: ' + error.message;
  }
})();
