sys_variable_value.value:
(function execute(inputs, outputs) {
    var util = new global.UtilBaseAIAB();
    var text = inputs.incomingtext;
    var chunkSize = parseInt(inputs.incomingchunksize, 10);
    outputs.chunks = util.chunkText(text, chunkSize);
})(inputs, outputs);

