sys_variable_value.value:
(function execute(inputs, outputs) {
    var text = inputs.incomingtext;
    var chunkSize = parseInt(inputs.incomingchunksize,10);
    var chunkText = new aiabChunkText();
    outputs.chunks = chunkText.toArray(text, chunkSize);
})(inputs, outputs);

