sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  if (isLoading || newValue === '') {
    return;
  }
  if(g_form.getValue('u_record_a')){
	var ga = new GlideAjax('aiabAjax');
	ga.addParam('sysparm_name', 'getSummarizeTemplate');
	ga.addParam('sysparm_table', g_form.getValue('u_table'));
	ga.addParam('sysparm_sys_id', g_form.getValue('u_record_a'));
	ga.addParam('sysparm_template', newValue);
	ga.getXMLAnswer(function (response) {
		g_form.setValue('u_record_a_data', response);
	});
  }
  if(g_form.getValue('u_record_b')){
	var gb = new GlideAjax('aiabAjax');
	gb.addParam('sysparm_name', 'getSummarizeTemplate');
	gb.addParam('sysparm_table', g_form.getValue('u_table'));
	gb.addParam('sysparm_sys_id', g_form.getValue('u_record_b'));
	gb.addParam('sysparm_template', newValue);
	gb.getXMLAnswer(function (response) {
		g_form.setValue('u_record_b_data', response);
	});
  }
}
