sys_script_client.script:
function onLoad() {
    var isNewRecord = g_form.isNewRecord();
    var isClearFormMessagesAvailable = typeof g_form.clearFormMessages !== "undefined";

    var clearMessage = function () {
        if (isClearFormMessagesAvailable) {
            g_form.clearFormMessages("info");
        } else {
            g_form.clearMessages();
        }
    };

    var addMessage = function (message) {
        if (isClearFormMessagesAvailable) {
            g_form.addFormMessage(message);
        } else {
            var modified = message.replace(/\n/gm, '<br/>');
            g_form.addInfoMessage("<br/>" + modified);
        }
    };

    var result = "";

    if (isNewRecord) {
        return;
    }

    var ga = new GlideAjax("aiabAjax");
    ga.addParam("sysparm_name", "getInferenceRecord");
    ga.addParam("sysparm_sys_id", g_form.getUniqueValue());
    ga.getXMLAnswer(function (response) {
        clearMessage();
        if (response != 'loading') {
            if (response != 'false') {
                result = "success";
                addMessage(response);
            } else {
                result = "false";
            }
        } else {
            //addMessage("Fetching Summary");
            //we're not checking here... becasue it competes with summarize ui aciton
        }
    });
}
