sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  if (isLoading || newValue === '') {
    return;
  }
  var ga = new GlideAjax('aiabAjax');
  ga.addParam('sysparm_name', 'getSummarizeTemplate');
  ga.addParam('sysparm_table', 'incident');
  ga.addParam('sysparm_sys_id', g_form.getValue('u_record_a'));
  ga.getXMLAnswer(function (response) {
    response = response.replace(/\\n/g, '<br/>');
    g_form.showFieldMsg('u_record_a', response);
  });
}
