sys_ui_action.client_script_v2:
function onClick(g_form) {
    var table = g_form.getTableName();
    var sysId = g_form.getUniqueValue();
    var display = g_form.getValue('number') || g_form.getValue('name') || '';

    var url = '/aiab_chat_modal.do?sysparm_modal=true&sysparm_table=' + encodeURIComponent(table) + '&sysparm_record=' + encodeURIComponent(sysId);
    if (display) {
        url += '&sysparm_display=' + encodeURIComponent(display);
    }

    g_modal.showFrame({
        url: url,
        size: 'lg',
        title: 'AI Chat',
        height: '600px'
    });
}

sys_ui_action.script:
function openAIABChat() {
	var gm = new GlideModal('aiab_chat_modal', false, 600);
	gm.setTitle('AI Chat');
	gm.setPreference('sysparm_modal', 'true');
	gm.setPreference('sysparm_table', g_form.getTableName());
	gm.setPreference('sysparm_record', g_form.getUniqueValue());
	gm.setPreference('sysparm_display', g_form.getValue('number') || g_form.getValue('name') || '');
	gm.render();
}
