sys_ui_action.script:
var toolName = current.u_name + '';
if (!toolName) {
    gs.addInfoMessage('Tool name is empty');
    action.setRedirectURL(current);
    return;
}

// Search the Execute Tool REST endpoint for this tool's handler
var gr = new GlideRecord('sys_ws_operation');
gr.addQuery('name', 'Execute Tool');
gr.addQuery('sys_scope', '0439d1f593948210071230a97bba1016'); // AI in a Box scope
gr.query();

if (gr.next()) {
    var script = gr.operation_script + '';

    // Check if tool is in the switch statement
    var searchPattern = "case '" + toolName + "':";
    var foundIndex = script.indexOf(searchPattern);

    if (foundIndex > -1) {
        // Find the function name
        var afterCase = script.substring(foundIndex);
        var executeMatch = afterCase.match(/result = (execute\w+)/);

        if (executeMatch) {
            var funcName = executeMatch[1];
            // Find line number (approximately)
            var beforeMatch = script.substring(0, foundIndex);
            var lineNumber = beforeMatch.split('\n').length;

            gs.addInfoMessage('Tool "' + toolName + '" is handled by function: ' + funcName + ' (around line ' + lineNumber + ' in Execute Tool REST endpoint)');
        } else {
            gs.addInfoMessage('Tool "' + toolName + '" found in Execute Tool endpoint but handler function unclear');
        }
    } else {
        gs.addErrorMessage('Tool "' + toolName + '" NOT FOUND in Execute Tool endpoint. You need to add a case statement and handler function.');
    }
} else {
    gs.addErrorMessage('Execute Tool REST endpoint not found');
}

action.setRedirectURL(current);
