sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {//
    var returnObj = {};
    var streamingAllowed = gs.getProperty('ai_in_a_box.feature.stream.enabled', 'true');
    if (!streamingAllowed) {
        return {
            status: "Streaming disabled"
        };
    }
    var serverSecret = gs.getProperty('ai_in_a_box.feature.stream.auth', '');
    var requestSecret = request.body.data.secret;
    if (!requestSecret) {
        return {
            status: "Secret not set"
        };
    }
    var matchingSecret = requestSecret == serverSecret;
    if (!matchingSecret) {
        return {
            status: "Secret not matched"
        };
    }
    var inferenceId = request.body.data.inferenceId;
    if (!inferenceId) {
        return {
            status: "inferenceId not set",
			debug: request.body.data
        };
    }
    var contents = request.body.data.contents;
	var finished = request.body.data.finished;
    if (!contents && finished === null) {
        return {
            status: "contents not set"
        };
    }

    returnObj.status = "Permitted";
    var inference = new GlideRecord('u_ai_inference');
    inference.addQuery('sys_id', inferenceId);
    inference.setLimit(1);
    inference.query();
	if (inference.hasNext() == false){
		return {
			status: "Invalid Inference",
			debug: request.body.data,
			link: 'https://' + gs.getProperty('instance_name') + '.service-now.com/u_ai_inference_list.do?sysparm_query=' + inference.getEncodedQuery()
		};
	}
    if (inference.next()) {
        returnObj.status = "Valid";
        var responseField = inference.getValue('u_response') || '';
        inference.setValue('u_response', responseField + contents);
		if(finished === 'stop'){
			inference.setValue('u_state','finished');
		}
        var updated = inference.update();
        if (updated) {
            returnObj.status = "Success";
			returnObj.update = updated;
			returnObj.state = inference.getValue('u_state');
        }
    }
    response.setBody(returnObj);
})(request, response);
