sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // Auth check - reuse the same secret as streaming
    var serverSecret = gs.getProperty('ai_in_a_box.feature.stream.auth', '');
    var requestSecret = request.getHeader('x-aiab-secret') || (request.body.data && request.body.data.secret);

    if (!serverSecret || !requestSecret || requestSecret !== serverSecret) {
        response.setStatus(401);
        return { error: 'Unauthorized' };
    }

    // Get request body
    var body = request.body.data;
    var toolName = body.tool_name;
    var toolArguments = body.arguments || {};
    var userId = body.user_id;  // sys_id of user to impersonate
    var inferenceId = body.inference_id;  // for context/logging

    if (!toolName) {
        response.setStatus(400);
        return { error: 'tool_name is required' };
    }

    // Look up the tool definition (Phase 2c will add u_ai_tool table)
    // For now, we'll hardcode some test tools
    var result;
    var error;

    try {
        // TODO: Replace with u_ai_tool lookup in Phase 2c
        if (toolName === 'get_incident') {
            result = executeGetIncident(toolArguments, userId);
        } else if (toolName === 'get_current_user') {
            result = executeGetCurrentUser(userId);
        } else {
            response.setStatus(404);
            return { error: 'Unknown tool: ' + toolName };
        }
    } catch (e) {
        error = e.message || String(e);
        response.setStatus(500);
        return { error: error };
    }

    return {
        tool_name: toolName,
        result: result
    };

    // --- Tool implementations ---

    function executeGetIncident(args, impersonateUserId) {
        var number = args.number;
        if (!number) {
            throw new Error('number argument is required');
        }

        // Impersonate user to respect ACLs
        var originalUser = null;
        if (impersonateUserId) {
            originalUser = gs.getUserID();
            gs.setCurrentUser(impersonateUserId);
        }

        try {
            var gr = new GlideRecord('incident');
            gr.addQuery('number', number);
            gr.setLimit(1);
            gr.query();

            if (!gr.next()) {
                return { found: false, message: 'Incident not found: ' + number };
            }

            // Check if user can read this record
            if (!gr.canRead()) {
                return { found: false, message: 'Access denied to incident: ' + number };
            }

            return {
                found: true,
                sys_id: gr.getUniqueValue(),
                number: gr.getValue('number'),
                short_description: gr.getValue('short_description'),
                state: gr.getDisplayValue('state'),
                priority: gr.getDisplayValue('priority'),
                assigned_to: gr.getDisplayValue('assigned_to'),
                caller_id: gr.getDisplayValue('caller_id')
            };
        } finally {
            // Restore original user
            if (originalUser) {
                gs.setCurrentUser(originalUser);
            }
        }
    }

    function executeGetCurrentUser(impersonateUserId) {
        if (impersonateUserId) {
            gs.setCurrentUser(impersonateUserId);
        }

        return {
            sys_id: gs.getUserID(),
            user_name: gs.getUserName(),
            name: gs.getUserDisplayName(),
            email: gs.getUser().getEmail()
        };
    }

})(request, response);
