u_ai_tool.u_script:
(function(args, userId) {
    var gr = new GlideRecord("kb_knowledge");
    gr.addQuery("workflow_state", "published");

    if (args.query) {
        var qc = gr.addQuery("short_description", "CONTAINS", args.query);
        qc.addOrCondition("text", "CONTAINS", args.query);
    }
    if (args.category) {
        var catGR = new GlideRecord("kb_category");
        catGR.addQuery("label", "CONTAINS", args.category);
        catGR.setLimit(1);
        catGR.query();
        if (catGR.next()) gr.addQuery("kb_category", catGR.getUniqueValue());
    }

    gr.orderByDesc("sys_view_count");
    gr.setLimit(Math.min(args.limit || 5, 10));
    gr.query();

    var articles = [];
    while (gr.next()) {
        var text = "" + gr.text;
        articles.push({
            number: "" + gr.number,
            title: "" + gr.short_description,
            category: gr.getDisplayValue("kb_category"),
            views: "" + gr.sys_view_count,
            excerpt: text.substring(0, 200) + "..."
        });
    }

    if (articles.length === 0) return { found: false, message: "No knowledge articles found" };
    return { found: true, count: articles.length, articles: articles };
})(args, userId);
