u_ai_tool.u_parameters:
{
  "type": "object",
  "properties": {
    "query": {
      "type": "string",
      "description": "Search text to find in article title or content"
    },
    "category": {
      "type": "string",
      "description": "Knowledge category name to filter by"
    },
    "published_recently": {
      "type": "boolean",
      "description": "If true, only return articles published in the last 30 days"
    },
    "limit": {
      "type": "integer",
      "description": "Max articles to return (default 5, max 10)"
    }
  },
  "required": []
}

u_ai_tool.u_script:
(function(args, userId) {
    var kbGR = new GlideRecord("kb_knowledge");
    kbGR.addQuery("workflow_state", "published");

    if (args.query) {
		var query = "short_descriptionCONTAINS" + args.query;
		query += "textCONTAINS" + args.query;
        gr.addEncodedQuery(query);
    }
    if (args.category) {
        var catGR = new GlideRecord("kb_category");
        catGR.addQuery("label", "CONTAINS", args.category);
        catGR.setLimit(1);
        catGR.query();
        if (catGR.next()) {
			kbGR.addQuery("kb_category", catGR.getUniqueValue());
		}
    }

    kbGR.orderByDesc("sys_view_count");
    kbGR.setLimit(Math.min(args.limit || 5, 10));
    kbGR.query();

    var articles = [];
    while (kbGR.next()) {
        var text = "" + kbGR.text;
		var toMD = new aiabHtmlToMarkdown();
		var markdown = toMD.convert(text);
        articles.push({
            number: "" + gr.number,
			link: "/kb_view.do?sys_kb_id=" + gr.getValue('sys_id'),
            title: "" + gr.short_description,
            category: gr.getDisplayValue("kb_category"),
            views: "" + gr.sys_view_count,
            excerpt: markdown.substring(0, 200) + "..."
        });
    }

    if (articles.length === 0) return { found: false, message: "No knowledge articles found" };
    return { found: true, count: articles.length, articles: articles };
})(args, userId);
