u_ai_tool.u_parameters:
{
  "type": "object",
  "properties": {
    "query": {
      "type": "string",
      "description": "Search text to find in catalog item name or description"
    },
    "category": {
      "type": "string",
      "description": "Catalog category name to filter by"
    },
    "limit": {
      "type": "integer",
      "description": "Max items to return (default 5, max 10)"
    },
    "portal_suffix": {
      "type": "string",
      "description": "The portal suffix e.g. sp of service-now.com/sp the user is in and blank for core ui"
    }
  },
  "required": []
}

u_ai_tool.u_script:
(function(args, userId) {
	var instanceLink = "https://" + gs.getProperty("instance_name") + ".service-now.com";
	if (typeof args.limit == "undefined") args.limit = 3;
    if (typeof args.query == "undefined") return {
        error: "No query included"
    };
    var itemGR = new GlideRecord("sc_cat_item");
    itemGR.addQuery("active", true);
    itemGR.addQuery('IR_AND_OR_QUERY', args.query);
    if (args.category) {
        var catGR = new GlideRecord("sc_category");
        catGR.addQuery("title", "CONTAINS", args.category);
        catGR.setLimit(1);
        catGR.query();
        if (catGR.next()) {
			itemGR.addQuery("category", catGR.getUniqueValue());
		}
    }

    itemGR.setLimit(parseInt(args.limit,10));
    itemGR.query();

    var items = [];
    while (itemGR.next()) {
		var link = false;
		var sysid = itemGR.getValue("sys_id");
		if(args.portal_suffix){
			link = instanceLink + "/" + args.portal_suffix + "?id=sc_cat_item&sys_id=" + sysid;
		} else {
			link = instanceLink + "/now/nav/ui/classic/params/target/com.glideapp.servicecatalog_cat_item_view.do?v=1&sysparm_id=" + sysid;
		}

        items.push({
            name: itemGR.name + "",
            short_description: itemGR.short_description + "",
            category: sysid.getDisplayValue("category"),
			link: link
        });
    }

    if (items.length === 0) return { found: false, message: "No catalog items found" };
    return { found: true, count: items.length, items: items };
})(args, userId);
