u_ai_tool.u_parameters:
{
    "type": "object",
    "properties": {
        "caller_sys_id": {
            "type": "string",
            "description": "sys_id of the caller (from current incident context)"
        },
        "caller_name": {
            "type": "string",
            "description": "Name of the caller to search for"
        },
        "exclude_number": {
            "type": "string",
            "description": "Incident number to exclude (usually the current incident)"
        },
        "state": {
            "type": "string",
            "enum": [
                "open",
                "resolved",
                "closed",
                "all"
            ],
            "description": "Filter by state: open (new/in_progress/on_hold), resolved, closed, or all"
        },
        "days_back": {
            "type": "integer",
            "description": "How many days back to search (default 90, max 365)"
        },
        "limit": {
            "type": "integer",
            "description": "Max incidents to return (default 5, max 10)"
        },
       "similar_incidents_by_category": {
           "type": "string",
           "description": "find incidents with the same category"
       }
    },
    "required": []
}

u_ai_tool.u_script:
(function(args, userId) {
    var gr = new GlideRecordSecure('incident');

    // Must have caller_sys_id or caller_name
    if (args.caller_sys_id) {
        gr.addQuery('caller_id', args.caller_sys_id);
    } else if (args.caller_name) {
        gr.addQuery('caller_id.name', 'LIKE', args.caller_name);
    } else {
        return { found: false, message: 'Either caller_sys_id or caller_name is required' };
    }

    // Exclude current incident if provided
    if (args.exclude_number) {
        gr.addQuery('number', '!=', args.exclude_number);
    }

    // State filter
    var state = (args.state || 'open').toLowerCase();
    if (state === 'open') {
        gr.addQuery('state', 'IN', '1,2,3'); // new, in_progress, on_hold
    } else if (state === 'resolved') {
        gr.addQuery('state', '6');
    } else if (state === 'closed') {
        gr.addQuery('state', '7');
    }
    // 'all' = no state filter

    // Days back filter
    var daysBack = Math.min(args.days_back || 90, 365);
    var cutoffDate = new GlideDateTime();
    cutoffDate.addDaysLocalTime(-daysBack);
    gr.addQuery('opened_at', '>=', cutoffDate);

	// similar incidents
	if(args.similar_incidents_by_category){
		//https://dev184755.service-now.com/sys_choice_list.do?sysparm_query=element%3Dcategory%5Ename%3Dincident%5Elabel%3DInquiry%20%2F%20Help&sysparm_view=
		var choicesGR = new GlideRecord('sys_choice');
		choicesGR.addQuery('element', 'category');
		choicesGR.addQuery('name', 'incident');
		choicesGR.addQuery('label', args.similar_incidents_by_category);
		choicesGR.addQuery('language', 'en');
		choicesGR.setLimit(1);
		choicesGR.query();
		if(choicesGR.next()){
			gr.addQuery('category', choicesGR.getValue('value'));
		}
	}

    gr.orderByDesc('opened_at');
    gr.setLimit(Math.min(args.limit || 5, 10));
    gr.query();

    var incidents = [];
    while (gr.next()) {
        incidents.push({
            number: '' + gr.number,
            short_description: '' + gr.short_description,
            state: gr.getDisplayValue('state'),
            priority: gr.getDisplayValue('priority'),
            opened_at: '' + gr.opened_at,
            resolved_at: '' + gr.resolved_at
        });
    }

    if (incidents.length === 0) {
        return { found: false, count: 0, message: 'No related incidents found for this caller' };
    }
    return { found: true, count: incidents.length, incidents: incidents };
})(args, userId);
